int search(int *array, int left, int right, int element){
    if(right >= left){
        int mid = left + (right - left) / 2;

        if(array[mid] == element){
            return mid;
        }else if( array[mid] > element ){
            return search(array, left, mid-1, element);
        }else{
            return search(array, mid+1, right, element);
        }
    }

    return -1;
}

